INSERT INTO `#__rsdirectory_email_messages` (`id`, `description`, `category_id`, `type`, `entry_expiration_period`, `to_name`, `to_email`, `bcc`, `cc`, `subject`, `send_html`, `text`, `checked_out`, `checked_out_time`, `published`) VALUES
(1, 'Message to entry author on entry publish', 0, 'publish_entry', 0, '{name}', '{email}', '', '', 'Your entry "{title}" has been approved!', 1, '<p>Hello {name}!</p>\r\n<p>Your entry, <a href="{url}">{title}</a>, has been approved and will be displayed on the site starting {publishing-date}.</p>\r\n<p>Kind Regards,<br /><a href="{site.url}">{site.name}</a> staff</p>', 0, '0000-00-00 00:00:00', 0),
(2, 'Message to entry author on entry unpublish', 0, 'unpublish_entry', 0, '{name}', '{email}', '', '', 'Your entry "{title}" has been unapproved!', 1, '<p>Hello {name}!</p>\r\n<p>Your entry, <a href="{url}">{title}</a>, has been unapproved and will no longer be displayed on the site.</p>\r\n<p>Kind Regards,<br /><a href="{site.url}">{site.name}</a> staff</p>', 0, '0000-00-00 00:00:00', 0),
(3, 'Message to entry author on entry delete', 0, 'delete_entry', 0, '{name}', '{email}', '', '', 'Your entry "{title}" has been deleted!', 1, '<p>Hello {name}!</p>\r\n<p>Your entry from <a href="{site.url}">{site.name}</a>, {title}, has been deleted.</p>\r\n<p>Kind Regards,<br /><a href="{site.url}">{site.name}</a> staff</p>', 0, '0000-00-00 00:00:00', 0),
(4, 'Message to entry author 48 hours prior to entry expiration', 0, 'entry_expiration_notice', 48, '{name}', '{email}', '', '', 'Your entry "{title}" will expire on {expiry-date}', 1, '<p>Hello {name}!</p>\r\n<p>Your entry, <a href="{url}">{title}</a>, will expire on {expiry-date}.</p>\r\n<p>Kind Regards,<br /><a href="{site.url}">{site.name}</a> staff</p>', 0, '0000-00-00 00:00:00', 0),
(5, 'Message to entry author on entry expiration', 0, 'entry_expiration', 0, '{name}', '{email}', '', '', 'Your entry "{title}" has expired!', 1, '<p>Hello {name}!</p>\r\n<p>Your entry, <a href="{url}">{title}</a>, has expired and will no longer be displayed on the web site!</p>\r\n<p>Kind Regards,<br /><a href="{site.url}">{site.name}</a> staff</p>', 0, '0000-00-00 00:00:00', 0);